<?php

namespace FBN\GuideBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * EventRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EventRepository extends EntityRepository
{
    public function getArticlesImages($first = 0, $limit = Article::NUM_ITEMS)
    {
        $qb = $this->createQueryBuilder('e')
            ->leftJoin('e.image', 'i')
            ->addSelect('i')
            ->orderBy('e.datePublication', 'DESC')
            ->andWhere('e.publication = :publication')
            ->setParameter('publication', true);

        $query = $qb->getQuery();

        $query->setFirstResult($first)
            ->setMaxResults($limit);

        return $query->getResult();
    }

    public function getEventsWithExcludedId($id = 0)
    {
        return $this->createQueryBuilder('e')
            ->where('e.id != :id')
            ->setParameter('id', $id);
    }

    public function getEvent($slug)
    {
        $qb = $this->createQueryBuilder('e')
            ->leftJoin('e.image', 'i')
            ->addSelect('i')
            ->leftJoin('e.eventType', 'et')
            ->addSelect('et')
            ->leftJoin('e.eventPast', 'ep')
            ->addSelect('ep')
            ->leftJoin('e.restaurant', 'er')
            ->addSelect('er')
            ->leftJoin('e.shop', 'ec')
            ->addSelect('ec')
            ->leftJoin('e.winemakerDomain', 'ev')
            ->addSelect('ev')
            ->leftJoin('ev.winemaker', 'evv')
            ->addSelect('evv')
            ->leftJoin('e.coordinates', 'c')
            ->addSelect('c')
            ->andWhere('e.publication = :publication')
            ->setParameter('publication', true)
            ->andWhere('e.slug = :slug')
            ->setParameter('slug', $slug);

        $cr = $this->_em
            ->getRepository('FBNGuideBundle:Coordinates');

        $qb = $cr->joinCoord($qb);

        return $qb->getQuery()
            ->getOneOrNullResult();
    }
}
